mapper.select:
SELECT c.comment_id, c.post_id, c.from_user_id, c.to_comment_id, c.comment_content, c.comment_time, c.comment_status
FROM lc_comment c
WHERE c.comment_status = #{status}
ORDER BY c.comment_id
LIMIT #{offSet}, #{pageRow}

SELECT c.comment_id, c.post_id, c.from_user_id, c.to_comment_id, c.comment_content, c.comment_time, c.comment_status
FROM lc_comment c
WHERE c.from_user_id = #{id}
ORDER BY c.comment_id
LIMIT #{offSet}, #{pageRow}

SELECT c.comment_id, c.post_id, c.from_user_id, c.to_comment_id, c.comment_content, c.comment_time, c.comment_status
FROM lc_comment c
WHERE c.post_id = #{id}
ORDER BY c.comment_id
LIMIT #{offSet}, #{pageRow}

SELECT c.comment_id, c.post_id, c.from_user_id, c.to_comment_id, c.comment_content, c.comment_time, c.comment_status
FROM lc_comment c
WHERE c.from_user_id = #{id} AND c.comment_status = #{status}
ORDER BY c.comment_id
LIMIT #{offSet}, #{pageRow}

SELECT c.comment_id, c.post_id, c.from_user_id, c.to_comment_id, c.comment_content, c.comment_time, c.comment_status
FROM lc_comment c
WHERE c.post_id = #{id} AND c.comment_status = #{status}
ORDER BY c.comment_id
LIMIT #{offSet}, #{pageRow}

SELECT c.comment_id, c.post_id, c.from_user_id, c.to_comment_id, c.comment_content, c.comment_time, c.comment_status
FROM lc_comment c
WHERE c.comment_id = #{id}

SELECT COUNT(0)
FROM lc_comment
WHERE c.post_id = #{id}

mapper.update:
UPDATE lc_comment
SET lc_comment.comment_status = #{status}
WHERE lc_comment.comment_id = #{id}
