mapper.select:
SELECT c.comment_id id, c.post_id pid, c.from_user_id fuid, c.to_comment_id tcid, c.comment_content content, date_format(c.comment_time, '%Y.%m.%d %T') ctime, c.comment_status status, u.user_name uname, u.user_state ustate FROM lc_comment c, fly_user u WHERE c.from_user_id = u.user_id
AND c.comment_status = #{status}
AND c.from_user_id = #{uid}
AND c.post_id = #{pid}
ORDER BY c.comment_id LIMIT #{offSet}, #{pageRow}

SELECT COUNT(0)
  FROM lc_comment c, fly_user u
  WHERE c.from_user_id = u.user_id

      AND c.comment_status = #{status}


      AND c.from_user_id = #{uid}


      AND c.post_id = #{pid}

mapper.update:
UPDATE lc_comment
SET lc_comment.comment_status = #{status}
WHERE lc_comment.comment_id = #{cid}
