mapper.update:
update cqy_post_collect
set display = #{display}
where post_collect_id= #{postCollectId}

mapper.select:
select count(0)
from cqy_post_collect
where post_id= #{postId} and user_id=#{userId}

select display
from cqy_post_collect
where post_id= #{postId} and user_id=#{userId}

select count(0)
from tyn_post
where post_id= #{postId}

select count(0)
from fly_user
where user_id= #{userId}
