mapper.select:
select oop.*, comment_id commentId, from_user_id commentUserId, comment_content commentText, user_name commentName from ( SELECT op.*, img imgUrl, user_name good, (SELECT COUNT(0) FROM fly_browse b WHERE b.post_id=op.tId)+viewOff seepeople from( SELECT tyn_post.id tId, post_owner uId, user_name username, user_photo avatarUrl, DATE_FORMAT(post_time, '%Y.%m.%d %T') time, post_location address, post_content descr, categories_name types, view_offset viewOff FROM tyn_post,fly_user,cqy_post_categories,tyn_post_tag tpt
delete_state =1 and user_id=post_owner and categories_id=post_sort
and is_top like #{isTop}
and id=tpt.post_id and tag_type=#{tagType}
LIMIT #{pageNum}, 10 ) op left join tyn_post_img tpi on tId=tpi.post_id left join fly_like fl on tId=fl.post_id and like_state=1 left join fly_user fu on onuser_id=fu.user_id )oop left join lc_comment lc on tId=lc.post_id and comment_status in (0,1) left join fly_user on from_user_id = user_id ORDER BY if(#{getType}=4,seepeople,time) Desc

select oop.*, comment_id commentId, from_user_id commentUserId, comment_content commentText, user_name commentName from( SELECT op.*, img imgUrl, user_name good, (SELECT COUNT(0) FROM fly_browse b WHERE b.post_id=op.tId)+viewOff seepeople from( SELECT tyn_post.id tId, post_owner uId, user_name username, user_photo avatarUrl, DATE_FORMAT(post_time, '%Y.%m.%d %T') time, post_location address, post_content descr, categories_name types, view_offset viewOff FROM tyn_post,fly_user,cqy_post_categories,tyn_post_tag tpt
delete_state =1 and user_id=post_owner and categories_id=post_sort
and is_top not like #{isTop}
and id=tpt.post_id and tag_type=#{tagType}
and categories_name='月租短租'
LIMIT #{pageNum}, 10 ) op left join tyn_post_img tpi on tId=tpi.post_id left join fly_like fl on tId=fl.post_id and like_state=1 left join fly_user fu on onuser_id=fu.user_id )oop left join lc_comment lc on tId=lc.post_id and comment_status in (0,1) left join fly_user on from_user_id = user_id ORDER BY if(#{getType}=4,seepeople,time) Desc

select business_subway_id
from cqy_business_subway
where business_subway_name=#{tagName}
